import React, { useState, useEffect } from "react";
import { Form, Button, Card, Row, Col, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { FaUser, FaBuilding, FaUniversity, FaFileAlt } from "react-icons/fa";

const ApplicationForm = ({ role }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [fileSelected, setFileSelected] = useState(false);
  const [fileName, setFileName] = useState("");
  
  const [formData, setFormData] = useState({
    businessName: "",
    pan: "",
    email: "",
    phone: "",
    province: "",
    district: "",
    category: "", // Only required for centers
    contactPerson1: {
      name: "",
      phone: "",
    },
    contactPerson2: {
      name: "",
      phone: "",
    },
    bankDetails: {
      bankName: "",
      accountNumber: "",
      branch: "",
      accountHolderName: "",
    },
    panDocument: null,
    applicationType: role,
  });

  // List of provinces in Nepal
  const provinces = [
    "Province 1",
    "Madhesh Province",
    "Bagmati Province",
    "Gandaki Province",
    "Lumbini Province",
    "Karnali Province",
    "Sudurpashchim Province",
  ];

  // Categories for centers
  const centerCategories = [
    "Government",
    "Private",
    "Non-Profit",
    "Educational",
    "Healthcare",
    "Other"
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    // Handle nested objects
    if (name.includes(".")) {
      const [parent, child] = name.split(".");
      setFormData({
        ...formData,
        [parent]: {
          ...formData[parent],
          [child]: value,
        },
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({
        ...formData,
        panDocument: file,
      });
      setFileSelected(true);
      setFileName(file.name);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    // Validate form data
    if (!formData.businessName || !formData.pan || !formData.email || !formData.phone) {
      setError("Please fill in all required fields");
      setLoading(false);
      return;
    }

    // Validate PAN format (assuming PAN is a 9-digit number in Nepal)
    const panRegex = /^[0-9]{9}$/;
    if (!panRegex.test(formData.pan)) {
      setError("PAN must be a 9-digit number");
      setLoading(false);
      return;
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError("Please enter a valid email address");
      setLoading(false);
      return;
    }

    // Validate phone number (assuming 10-digit number for Nepal)
    const phoneRegex = /^[0-9]{10}$/;
    if (!phoneRegex.test(formData.phone)) {
      setError("Phone number must be a 10-digit number");
      setLoading(false);
      return;
    }

    // Validate category for centers
    if (role === "center" && !formData.category) {
      setError("Please select a category for your center");
      setLoading(false);
      return;
    }

    // Create form data for file upload
    const submitData = new FormData();
    for (const key in formData) {
      if (key === "contactPerson1" || key === "contactPerson2" || key === "bankDetails") {
        submitData.append(key, JSON.stringify(formData[key]));
      } else if (key === "panDocument" && formData[key]) {
        submitData.append("panDocument", formData[key]);
      } else {
        submitData.append(key, formData[key]);
      }
    }

    try {
      const res = await axios.post("/api/applications", submitData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setSuccess(
        `Your ${role === "vendor" ? "vendor" : "center"} application has been submitted successfully! We will review your application and get back to you soon.`
      );
      
      // Reset form after successful submission
      setFormData({
        businessName: "",
        pan: "",
        email: "",
        phone: "",
        province: "",
        district: "",
        category: "",
        contactPerson1: {
          name: "",
          phone: "",
        },
        contactPerson2: {
          name: "",
          phone: "",
        },
        bankDetails: {
          bankName: "",
          accountNumber: "",
          branch: "",
          accountHolderName: "",
        },
        panDocument: null,
        applicationType: role,
      });
      setFileSelected(false);
      setFileName("");
      
      // Redirect to dashboard after 3 seconds
      setTimeout(() => {
        navigate(`/${role}/dashboard`);
      }, 3000);
    } catch (err) {
      setError(
        err.response?.data?.message || "An error occurred while submitting your application"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card className="shadow border-0 rounded-lg">
      <Card.Body className="p-4">
        {error && <Alert variant="danger">{error}</Alert>}
        {success && <Alert variant="success">{success}</Alert>}

        <Form onSubmit={handleSubmit} className="registration-form">
          <div className="section-header">
            <FaBuilding className="section-icon" />
            <h5 className="mb-3 text-primary">Business Information</h5>
          </div>
          <Row className="mb-4">
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Email Address *</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="vendor@example.com"
                  required
                  className="form-control-lg"
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Phone Number *</Form.Label>
                <div className="input-group">
                  <span className="input-group-text">+977</span>
                  <Form.Control
                    type="text"
                    name="phone"
                    value={formData.phone}
                    onChange={handleChange}
                    placeholder="9876543210"
                    required
                    className="form-control-lg"
                  />
                </div>
                <small className="text-muted">* 10 digit mobile number</small>
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-4">
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Password *</Form.Label>
                <Form.Control
                  type="password"
                  name="password"
                  placeholder="Enter password"
                  required
                  className="form-control-lg"
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>District *</Form.Label>
                <Form.Select
                  name="district"
                  value={formData.district}
                  onChange={handleChange}
                  required
                  className="form-control-lg"
                >
                  <option value="">Select district</option>
                  {/* District options */}
                </Form.Select>
              </Form.Group>
            </Col>
          </Row>

          <div className="section-header mt-5">
            <FaUser className="section-icon" />
            <h5 className="mb-3 text-primary">Contact Information</h5>
          </div>
          <Row className="mb-4">
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Contact Person 1 *</Form.Label>
                <Form.Control
                  type="text"
                  name="contactPerson1.name"
                  value={formData.contactPerson1.name}
                  onChange={handleChange}
                  placeholder="Contact person name"
                  required
                  className="form-control-lg"
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Phone *</Form.Label>
                <Form.Control
                  type="text"
                  name="contactPerson1.phone"
                  value={formData.contactPerson1.phone}
                  onChange={handleChange}
                  placeholder="9876543210"
                  required
                  className="form-control-lg"
                />
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-4">
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Contact Person 2 (Optional)</Form.Label>
                <Form.Control
                  type="text"
                  name="contactPerson2.name"
                  value={formData.contactPerson2.name}
                  onChange={handleChange}
                  placeholder="Contact person name (optional)"
                  className="form-control-lg"
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Phone</Form.Label>
                <Form.Control
                  type="text"
                  name="contactPerson2.phone"
                  value={formData.contactPerson2.phone}
                  onChange={handleChange}
                  placeholder="9876543210"
                  className="form-control-lg"
                />
              </Form.Group>
            </Col>
          </Row>

          <div className="section-header mt-5">
            <FaUniversity className="section-icon" />
            <h5 className="mb-3 text-primary">Bank Details</h5>
          </div>
          <Row className="mb-4">
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Bank Name *</Form.Label>
                <Form.Select
                  name="bankDetails.bankName"
                  value={formData.bankDetails.bankName}
                  onChange={handleChange}
                  required
                  className="form-control-lg"
                >
                  <option value="">Select Bank</option>
                  <option value="Nepal Bank">Nepal Bank</option>
                  {/* Add more banks */}
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Account Number *</Form.Label>
                <Form.Control
                  type="text"
                  name="bankDetails.accountNumber"
                  value={formData.bankDetails.accountNumber}
                  onChange={handleChange}
                  placeholder="123456789012"
                  required
                  className="form-control-lg"
                />
              </Form.Group>
            </Col>
          </Row>

          <Row className="mb-4">
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Branch *</Form.Label>
                <Form.Control
                  type="text"
                  name="bankDetails.branch"
                  value={formData.bankDetails.branch}
                  onChange={handleChange}
                  placeholder="Branch name"
                  required
                  className="form-control-lg"
                />
              </Form.Group>
            </Col>
            <Col md={6}>
              <Form.Group className="mb-3">
                <Form.Label>Account Holder Name *</Form.Label>
                <Form.Control
                  type="text"
                  name="bankDetails.accountHolderName"
                  value={formData.bankDetails.accountHolderName}
                  onChange={handleChange}
                  placeholder="Account holder name"
                  required
                  className="form-control-lg"
                />
              </Form.Group>
            </Col>
          </Row>

          <div className="section-header mt-5">
            <FaFileAlt className="section-icon" />
            <h5 className="mb-3 text-primary">Documents</h5>
          </div>
          <Row className="mb-4">
            <Col md={12}>
              <Form.Group className="mb-3">
                <Form.Label>Upload Documents (PAN Card, Business Registration, etc.)</Form.Label>
                <div className="upload-box">
                  <div className="text-center p-4 border-dashed border-2 rounded-lg">
                    <div className="upload-icon mb-2">
                      <FaFileAlt size={24} className="text-primary" />
                    </div>
                    <p>Click to upload or drag and drop</p>
                    <p className="text-muted small">Choose files</p>
                    <Form.Control
                      type="file"
                      accept=".pdf,.jpg,.jpeg,.png"
                      onChange={handleFileChange}
                      required
                      className="d-none"
                      id="file-upload"
                    />
                    <label htmlFor="file-upload" className="btn btn-outline-primary mt-2">
                      Choose files
                    </label>
                  </div>
                  {fileSelected && (
                    <div className="mt-2">
                      <span className="text-success">Selected file: {fileName}</span>
                    </div>
                  )}
                </div>
              </Form.Group>
            </Col>
          </Row>

          <div className="application-process mt-5 mb-4 p-3 bg-light rounded">
            <h5 className="text-primary">Application Process</h5>
            <p className="mb-1">Your application will be submitted to the admin for review. You will receive an email notification once your application is approved or if additional information is required.</p>
          </div>

          <div className="d-flex justify-content-between mt-4">
            <Button
              variant="outline-secondary"
              onClick={() => navigate("/login")}
              className="px-4"
            >
              Cancel
            </Button>
            <Button
              variant="primary"
              type="submit"
              disabled={loading}
              className="px-4"
            >
              {loading ? "Submitting..." : "Submit Application"}
            </Button>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default ApplicationForm;
