import React, { useState, useEffect } from "react";
import { Form, Button, Row, Col, Alert, Card } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import {
  FaUser,
  FaBuilding,
  FaUniversity,
  FaFileAlt,
} from "react-icons/fa";

const VendorRegistrationForm = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [fileSelected, setFileSelected] = useState(false);
  const [fileName, setFileName] = useState("");
  const [districts, setDistricts] = useState([]);

  const [formData, setFormData] = useState({
    businessName: "",
    pan: "",
    email: "",
    password: "", // Add password field
    phone: "",
    province: "",
    district: "",
    contactPerson1: {
      name: "",
      phone: "",
    },
    contactPerson2: {
      name: "",
      phone: "",
    },
    bankDetails: {
      bankName: "",
      accountNumber: "",
      branch: "",
      accountHolderName: "",
    },
    panDocument: null,
    applicationType: "vendor",
  });

  // List of provinces in Nepal
  const provinces = [
    { id: "1", name: "Province 1" },
    { id: "2", name: "Madhesh Province" },
    { id: "3", name: "Bagmati Province" },
    { id: "4", name: "Gandaki Province" },
    { id: "5", name: "Lumbini Province" },
    { id: "6", name: "Karnali Province" },
    { id: "7", name: "Sudurpashchim Province" },
  ];

  // Fetch districts when province changes
  useEffect(() => {
    const fetchDistricts = async () => {
      if (formData.province) {
        try {
          const res = await axios.get(
            `/api/locations/provinces/${formData.province}/districts`
          );
          setDistricts(res.data);
        } catch (err) {
          console.error("Error fetching districts:", err);
        }
      } else {
        setDistricts([]);
      }
    };

    fetchDistricts();
  }, [formData.province]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    // Handle nested objects
    if (name.includes(".")) {
      const [parent, child] = name.split(".");
      setFormData({
        ...formData,
        [parent]: {
          ...formData[parent],
          [child]: value,
        },
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({
        ...formData,
        panDocument: file,
      });
      setFileSelected(true);
      setFileName(file.name);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    // Validate form data
    if (
      !formData.businessName ||
      !formData.pan ||
      !formData.email ||
      !formData.phone
    ) {
      setError("Please fill in all required fields");
      setLoading(false);
      return;
    }

    // Create form data for file upload
    const submitData = new FormData();
    for (const key in formData) {
      if (
        key === "contactPerson1" ||
        key === "contactPerson2" ||
        key === "bankDetails"
      ) {
        submitData.append(key, JSON.stringify(formData[key]));
      } else if (key === "panDocument" && formData[key]) {
        submitData.append("panDocument", formData[key]);
      } else {
        submitData.append(key, formData[key]);
      }
    }

    try {
      const res = await axios.post("/api/applications", submitData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setSuccess(
        "Your vendor application has been submitted successfully! We will review your application and get back to you soon."
      );

      // Reset form after successful submission
      setFormData({
        businessName: "",
        pan: "",
        email: "",
        password: "",
        phone: "",
        province: "",
        district: "",
        contactPerson1: {
          name: "",
          phone: "",
        },
        contactPerson2: {
          name: "",
          phone: "",
        },
        bankDetails: {
          bankName: "",
          accountNumber: "",
          branch: "",
          accountHolderName: "",
        },
        panDocument: null,
        applicationType: "vendor",
      });
      setFileSelected(false);
      setFileName("");

      // Redirect to dashboard after 3 seconds
      setTimeout(() => {
        navigate("/vendor/dashboard");
      }, 3000);
    } catch (err) {
      setError(
        err.response?.data?.message ||
          "An error occurred while submitting your application"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="form-container mx-auto p-3">
      {error && <Alert variant="danger">{error}</Alert>}
      {success && <Alert variant="success">{success}</Alert>}

      <Form onSubmit={handleSubmit}>
        <Card className="mb-4 border-0 shadow-sm bg-white">
          <Card.Body className="p-3">
            <div className="form-section mb-4 bg-white">
              <div className="section-header">
                <i>
                  <FaBuilding size={20} />
                </i>
                <h5>Vendor Information</h5>
              </div>
              <Row className="form-row">
                <Col md={6} className="form-field-container">
                  <Form.Group className="mb-3">
                    <Form.Label>Business Name *</Form.Label>
                    <Form.Control
                      type="text"
                      name="businessName"
                      value={formData.businessName}
                      onChange={handleChange}
                      placeholder="Enter business name"
                      required
                      className="form-control-lg"
                    />
                  </Form.Group>
                </Col>
                <Col md={6} className="form-field-container">
                  <Form.Group className="mb-3">
                    <Form.Label>PAN Number *</Form.Label>
                    <Form.Control
                      type="text"
                      name="pan"
                      value={formData.pan}
                      onChange={handleChange}
                      placeholder="Enter 9-digit PAN number"
                      required
                      className="form-control-lg"
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row className="form-row">
                <Col md={6} className="form-field-container">
                  <Form.Group className="mb-3">
                    <Form.Label>Email Address *</Form.Label>
                    <Form.Control
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      placeholder="Enter email address"
                      required
                      className="form-control-lg"
                    />
                  </Form.Group>
                </Col>
                <Col md={6} className="form-field-container">
                  <Form.Group className="mb-3">
                    <Form.Label>Password *</Form.Label>
                    <Form.Control
                      type="password"
                      name="password"
                      value={formData.password}
                      onChange={handleChange}
                      placeholder="Enter password"
                      required
                      className="form-control-lg"
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row className="form-row">
                <Col md={6} className="form-field-container">
                  <Form.Group className="mb-3">
                    <Form.Label>Phone Number *</Form.Label>
                    <div className="input-group">
                      <span className="input-group-text">+977</span>
                      <Form.Control
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        placeholder="Enter 10-digit phone number"
                        required
                        className="form-control-lg"
                      />
                    </div>
                  </Form.Group>
                </Col>
              </Row>

              <Row className="form-row">
                <Col md={6} className="form-field-container">
                  <Form.Group className="mb-3">
                    <Form.Label>Province *</Form.Label>
                    <Form.Select
                      name="province"
                      value={formData.province}
                      onChange={handleChange}
                      required
                      className="form-control-lg"
                    >
                      <option value="">Select Province</option>
                      {provinces.map((province) => (
                        <option key={province.id} value={province.id}>
                          {province.name}
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={6} className="form-field-container">
                  <Form.Group className="mb-3">
                    <Form.Label>District *</Form.Label>
                    <Form.Select
                      name="district"
                      value={formData.district}
                      onChange={handleChange}
                      required
                      className="form-control-lg"
                      disabled={!formData.province}
                    >
                      <option value="">Select District</option>
                      {districts.map((district) => (
                        <option key={district.id} value={district.name}>
                          {district.name}
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
              </Row>
            </div>

            {/* Add more sections as needed */}
            
            <div className="form-actions">
              <Button
                variant="outline-secondary"
                onClick={() => navigate("/login")}
                className="px-4 py-2"
              >
                Cancel
              </Button>
              <Button
                variant="primary"
                type="submit"
                disabled={loading}
                className="px-4 py-2"
              >
                {loading ? "Submitting..." : "Submit Application"}
              </Button>
            </div>
          </Card.Body>
        </Card>
      </Form>
    </div>
  );
};

export default VendorRegistrationForm;
