import React, { useContext } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Navbar as BootstrapNavbar, Nav, Container, Button, Badge } from 'react-bootstrap';
import { FaSignInAlt, FaUserPlus, FaTachometerAlt, FaSignOutAlt, FaStore, FaBuilding } from 'react-icons/fa';
import { AuthContext } from '../../context/AuthContext';

const Navbar = () => {
  const { isAuthenticated, user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <BootstrapNavbar className="navbar" expand="lg">
      <Container>
        <BootstrapNavbar.Brand as={Link} to="/" className="d-flex align-items-center">
          <img 
            src="/assets/images/vrs-logo.png" 
            alt="VRS Logo" 
            style={{ width: '40px', height: 'auto', marginRight: '10px' }} 
          />
          <span>Vendor Request System</span>
        </BootstrapNavbar.Brand>
        <BootstrapNavbar.Toggle aria-controls="basic-navbar-nav" />
        <BootstrapNavbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            {/* Removed Home link */}
            
            {isAuthenticated ? (
              <>
                {user?.role === 'admin' && (
                  <Nav.Link as={Link} to="/admin/dashboard" className="d-flex align-items-center">
                    <FaTachometerAlt className="me-1" /> Dashboard
                  </Nav.Link>
                )}
                {user?.role === 'vendor' && (
                  <Nav.Link as={Link} to="/vendor/dashboard" className="d-flex align-items-center">
                    <FaStore className="me-1" /> Dashboard
                  </Nav.Link>
                )}
                {user?.role === 'center' && (
                  <Nav.Link as={Link} to="/center/dashboard" className="d-flex align-items-center">
                    <FaBuilding className="me-1" /> Dashboard
                  </Nav.Link>
                )}
                <Button variant="light" className="ms-2 d-flex align-items-center" onClick={handleLogout}>
                  <FaSignOutAlt className="me-1" /> Logout
                </Button>
              </>
            ) : (
              <>
                <Button 
                  as={Link} 
                  to="/login" 
                  variant="outline-light" 
                  className="ms-2 d-flex align-items-center"
                >
                  <FaSignInAlt className="me-1" /> Login
                </Button>
                <Button 
                  as={Link} 
                  to="/register" 
                  variant="light" 
                  className="ms-2 d-flex align-items-center"
                >
                  <FaUserPlus className="me-1" /> Register
                </Button>
              </>
            )}
          </Nav>
        </BootstrapNavbar.Collapse>
      </Container>
    </BootstrapNavbar>
  );
};

export default Navbar;
