import React, { useState, useContext, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
    role: "", // Added role field
  });

  const [showModal, setShowModal] = useState(false);
  const { email, password, role } = formData; // Destructure role
  const { login, isAuthenticated, error, user, clearError } =
    useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    if (isAuthenticated) {
      if (user?.role === "admin") {
        navigate("/admin/dashboard");
      } else if (user?.role === "vendor") {
        navigate("/vendor/dashboard");
      } else if (user?.role === "center") {
        navigate("/center/dashboard");
      }
    }
  }, [isAuthenticated, navigate, user]);

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    clearError();
    await login(formData);
  };

  const handleRoleSelect = (role) => {
    setShowModal(false);
    navigate("/register", { state: { selectedRole: role } });
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-50 to-indigo-50 px-4 py-12">
      <div className="w-full max-w-md">
        <div className="bg-white rounded-xl shadow-2xl overflow-hidden transform transition-all hover:scale-[1.01] duration-300">
          <div className="px-8 pt-8 pb-10">
            {/* Logo - Updated path to match the correct filename */}
            <div className="flex justify-center mb-6">
              <img 
                src="/assets/images/vrslogo.png" 
                alt="VRS Logo" 
                className="w-40 h-auto drop-shadow-md transition-transform duration-300 hover:scale-105" 
              />
            </div>
            
            <h2 className="text-2xl font-bold text-center text-indigo-600 mb-8">Login</h2>

            {/* Error Alert */}
            {error && (
              <div className="mb-6 bg-red-50 border-l-4 border-red-500 p-4 rounded-md flex items-start">
                <span className="text-red-600 mr-2">⚠️</span>
                <p className="text-red-700 text-sm">{error}</p>
              </div>
            )}

            <form onSubmit={onSubmit} className="space-y-6">
              <div>
                <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                  Email Address
                </label>
                <input
                  id="email"
                  type="email"
                  name="email"
                  value={email}
                  onChange={onChange}
                  required
                  className="w-full px-4 py-3 rounded-lg bg-gray-50 border border-gray-200 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 focus:bg-white transition duration-200 outline-none"
                />
              </div>

              <div>
                <label htmlFor="password" className="block text-sm font-medium text-gray-700 mb-1">
                  Password
                </label>
                <input
                  id="password"
                  type="password"
                  name="password"
                  value={password}
                  onChange={onChange}
                  required
                  className="w-full px-4 py-3 rounded-lg bg-gray-50 border border-gray-200 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 focus:bg-white transition duration-200 outline-none"
                />
              </div>

              {/* Added User Role Dropdown */}
              <div>
                <label htmlFor="role" className="block text-sm font-medium text-gray-700 mb-1">
                  User Role
                </label>
                <select
                  id="role"
                  name="role"
                  value={role}
                  onChange={onChange}
                  className="w-full px-4 py-3 rounded-lg bg-gray-50 border border-gray-200 focus:border-indigo-500 focus:ring-2 focus:ring-indigo-200 focus:bg-white transition duration-200 outline-none"
                >
                  <option value="">Select Role</option>
                  <option value="vendor">Vendor</option>
                  <option value="center">Center</option>
                </select>
              </div>

              <button
                type="submit"
                className="w-full bg-gradient-to-r from-indigo-600 to-blue-500 text-white py-3 rounded-lg font-medium shadow-md hover:shadow-lg transform hover:-translate-y-0.5 transition duration-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                Login
              </button>
            </form>

            <div className="mt-8 pt-6 text-center border-t border-gray-100">
              <p className="text-gray-600 mb-3">Don't have an account?</p>
              <button
                onClick={() => setShowModal(true)}
                className="w-full py-3 border-2 border-indigo-500 text-indigo-600 rounded-lg font-medium hover:bg-indigo-50 transition duration-300 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
              >
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Role Selection Modal */}
      {showModal && (
        <div className="fixed inset-0 z-50 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen px-4 pt-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 transition-opacity" aria-hidden="true">
              <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
            </div>

            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">&#8203;</span>

            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-gradient-to-r from-indigo-600 to-blue-500 px-6 py-4">
                <div className="flex items-center justify-between">
                  <h3 className="text-lg font-medium text-white">Select Registration Type</h3>
                  <button
                    onClick={() => setShowModal(false)}
                    className="text-white hover:text-gray-200 focus:outline-none"
                  >
                    <span className="sr-only">Close</span>
                    <svg className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                    </svg>
                  </button>
                </div>
              </div>
              <div className="bg-white px-6 py-5">
                <p className="text-gray-500 mb-5">Please select the type of account you want to create:</p>
                <div className="space-y-4">
                  <button
                    onClick={() => handleRoleSelect("vendor")}
                    className="w-full text-left px-5 py-4 border-2 border-indigo-500 rounded-lg hover:bg-indigo-50 transition duration-200 focus:outline-none focus:ring-2 focus:ring-indigo-500"
                  >
                    <span className="block text-indigo-600 font-medium">Register as Vendor</span>
                    <span className="block mt-1 text-sm text-gray-500">
                      Submit applications to centers and track their status
                    </span>
                  </button>
                  <button
                    onClick={() => handleRoleSelect("center")}
                    className="w-full text-left px-5 py-4 border-2 border-green-500 rounded-lg hover:bg-green-50 transition duration-200 focus:outline-none focus:ring-2 focus:ring-green-500"
                  >
                    <span className="block text-green-600 font-medium">Register as Center</span>
                    <span className="block mt-1 text-sm text-gray-500">
                      Review and manage vendor applications
                    </span>
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Login;
