import React from "react";
import { Container, Row, Col } from "react-bootstrap";
import { useParams, Navigate } from "react-router-dom";
import ApplicationForm from "../forms/ApplicationForm";

const VendorCenterRegister = () => {
  const { role } = useParams();
  
  // Validate role parameter
  if (role !== "vendor" && role !== "center") {
    return <Navigate to="/register" />;
  }

  return (
    <Container className="py-5">
      <Row className="justify-content-center">
        <Col lg={10}>
          <div className="text-center mb-4">
            <img 
              src="/assets/images/vrslogo.png" 
              alt="VRS Logo" 
              style={{ width: '120px', height: 'auto' }} 
              className="mb-3"
            />
            <h2>{role === "vendor" ? "Vendor" : "Center"} Registration</h2>
            <p className="text-muted">
              {role === "vendor"
                ? "Complete this form to register as a vendor and start submitting applications to centers."
                : "Complete this form to register as a center and start receiving vendor applications."}
            </p>
          </div>
          
          <ApplicationForm role={role} />
        </Col>
      </Row>
    </Container>
  );
};

export default VendorCenterRegister;
