import React, { useState, useEffect } from "react";
import { Form, Button, Card, Row, Col, Alert } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { FaUser, FaBuilding, FaUniversity, FaFileAlt } from "react-icons/fa";

const ApplicationForm = ({ role }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [fileSelected, setFileSelected] = useState(false);
  const [fileName, setFileName] = useState("");
  
  const [formData, setFormData] = useState({
    businessName: "",
    pan: "",
    email: "",
    phone: "",
    province: "",
    district: "",
    category: "", // Only required for centers
    contactPerson1: {
      name: "",
      phone: "",
    },
    contactPerson2: {
      name: "",
      phone: "",
    },
    bankDetails: {
      bankName: "",
      accountNumber: "",
      branch: "",
      accountHolderName: "",
    },
    panDocument: null,
    applicationType: role,
  });

  // List of provinces in Nepal
  const provinces = [
    "Province 1",
    "Madhesh Province",
    "Bagmati Province",
    "Gandaki Province",
    "Lumbini Province",
    "Karnali Province",
    "Sudurpashchim Province",
  ];

  // Categories for centers
  const centerCategories = [
    "Government",
    "Private",
    "Non-Profit",
    "Educational",
    "Healthcare",
    "Other"
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    // Handle nested objects
    if (name.includes(".")) {
      const [parent, child] = name.split(".");
      setFormData({
        ...formData,
        [parent]: {
          ...formData[parent],
          [child]: value,
        },
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({
        ...formData,
        panDocument: file,
      });
      setFileSelected(true);
      setFileName(file.name);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    // Validate form data
    if (!formData.businessName || !formData.pan || !formData.email || !formData.phone) {
      setError("Please fill in all required fields");
      setLoading(false);
      return;
    }

    // Validate PAN format (assuming PAN is a 9-digit number in Nepal)
    const panRegex = /^[0-9]{9}$/;
    if (!panRegex.test(formData.pan)) {
      setError("PAN must be a 9-digit number");
      setLoading(false);
      return;
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError("Please enter a valid email address");
      setLoading(false);
      return;
    }

    // Validate phone number (assuming 10-digit number for Nepal)
    const phoneRegex = /^[0-9]{10}$/;
    if (!phoneRegex.test(formData.phone)) {
      setError("Phone number must be a 10-digit number");
      setLoading(false);
      return;
    }

    // Validate category for centers
    if (role === "center" && !formData.category) {
      setError("Please select a category for your center");
      setLoading(false);
      return;
    }

    // Create form data for file upload
    const submitData = new FormData();
    for (const key in formData) {
      if (key === "contactPerson1" || key === "contactPerson2" || key === "bankDetails") {
        submitData.append(key, JSON.stringify(formData[key]));
      } else if (key === "panDocument" && formData[key]) {
        submitData.append("panDocument", formData[key]);
      } else {
        submitData.append(key, formData[key]);
      }
    }

    try {
      const res = await axios.post("/api/applications", submitData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setSuccess(
        `Your ${role === "vendor" ? "vendor" : "center"} application has been submitted successfully! We will review your application and get back to you soon.`
      );
      
      // Reset form after successful submission
      setFormData({
        businessName: "",
        pan: "",
        email: "",
        phone: "",
        province: "",
        district: "",
        category: "",
        contactPerson1: {
          name: "",
          phone: "",
        },
        contactPerson2: {
          name: "",
          phone: "",
        },
        bankDetails: {
          bankName: "",
          accountNumber: "",
          branch: "",
          accountHolderName: "",
        },
        panDocument: null,
        applicationType: role,
      });
      setFileSelected(false);
      setFileName("");
      
      // Redirect to dashboard after 3 seconds
      setTimeout(() => {
        navigate(`/${role}/dashboard`);
      }, 3000);
    } catch (err) {
      setError(
        err.response?.data?.message || "An error occurred while submitting your application"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <Card className="shadow-sm border-0 rounded-lg overflow-hidden">
      <Card.Body className="p-4">
        {error && <Alert variant="danger">{error}</Alert>}
        {success && <Alert variant="success">{success}</Alert>}

        <Form onSubmit={handleSubmit}>
          <div className="form-container mx-auto">
            <div className="form-section mb-4">
              <h5 className="section-title border-bottom pb-2 mb-3">Business Information</h5>
              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Business Name *</Form.Label>
                    <Form.Control
                      type="text"
                      name="businessName"
                      value={formData.businessName}
                      onChange={handleChange}
                      placeholder="Enter business name"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>PAN Number *</Form.Label>
                    <Form.Control
                      type="text"
                      name="pan"
                      value={formData.pan}
                      onChange={handleChange}
                      placeholder="Enter 9-digit PAN number"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Email *</Form.Label>
                    <Form.Control
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleChange}
                      placeholder="Enter email address"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Phone *</Form.Label>
                    <div className="input-group">
                      <span className="input-group-text">+977</span>
                      <Form.Control
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        placeholder="Enter 10-digit phone number"
                        required
                        className="form-field"
                      />
                    </div>
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Province *</Form.Label>
                    <Form.Select
                      name="province"
                      value={formData.province}
                      onChange={handleChange}
                      required
                      className="form-field"
                    >
                      <option value="">Select Province</option>
                      {provinces.map((province) => (
                        <option key={province} value={province}>
                          {province}
                        </option>
                      ))}
                    </Form.Select>
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>District *</Form.Label>
                    <Form.Control
                      type="text"
                      name="district"
                      value={formData.district}
                      onChange={handleChange}
                      placeholder="Enter district"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
              </Row>

              {/* Category field for centers only */}
              {role === "center" && (
                <Row>
                  <Col md={12}>
                    <Form.Group className="mb-3">
                      <Form.Label>Center Category *</Form.Label>
                      <Form.Select
                        name="category"
                        value={formData.category}
                        onChange={handleChange}
                        required
                        className="form-field"
                      >
                        <option value="">Select Category</option>
                        {centerCategories.map((category) => (
                          <option key={category} value={category}>
                            {category}
                          </option>
                        ))}
                      </Form.Select>
                    </Form.Group>
                  </Col>
                </Row>
              )}
            </div>

            <div className="form-section mb-4">
              <h5 className="section-title border-bottom pb-2 mb-3">Contact Persons</h5>
              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Contact Person 1 Name *</Form.Label>
                    <Form.Control
                      type="text"
                      name="contactPerson1.name"
                      value={formData.contactPerson1.name}
                      onChange={handleChange}
                      placeholder="Enter contact person name"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Contact Person 1 Phone *</Form.Label>
                    <Form.Control
                      type="text"
                      name="contactPerson1.phone"
                      value={formData.contactPerson1.phone}
                      onChange={handleChange}
                      placeholder="Enter contact person phone"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Contact Person 2 Name (Optional)</Form.Label>
                    <Form.Control
                      type="text"
                      name="contactPerson2.name"
                      value={formData.contactPerson2.name}
                      onChange={handleChange}
                      placeholder="Enter contact person name"
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Contact Person 2 Phone (Optional)</Form.Label>
                    <Form.Control
                      type="text"
                      name="contactPerson2.phone"
                      value={formData.contactPerson2.phone}
                      onChange={handleChange}
                      placeholder="Enter contact person phone"
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
              </Row>
            </div>

            <div className="form-section mb-4">
              <h5 className="section-title border-bottom pb-2 mb-3">Bank Details</h5>
              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Bank Name *</Form.Label>
                    <Form.Control
                      type="text"
                      name="bankDetails.bankName"
                      value={formData.bankDetails.bankName}
                      onChange={handleChange}
                      placeholder="Enter bank name"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Account Number *</Form.Label>
                    <Form.Control
                      type="text"
                      name="bankDetails.accountNumber"
                      value={formData.bankDetails.accountNumber}
                      onChange={handleChange}
                      placeholder="Enter account number"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
              </Row>

              <Row>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Branch *</Form.Label>
                    <Form.Control
                      type="text"
                      name="bankDetails.branch"
                      value={formData.bankDetails.branch}
                      onChange={handleChange}
                      placeholder="Enter branch name"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
                <Col md={6}>
                  <Form.Group className="mb-3">
                    <Form.Label>Account Holder Name *</Form.Label>
                    <Form.Control
                      type="text"
                      name="bankDetails.accountHolderName"
                      value={formData.bankDetails.accountHolderName}
                      onChange={handleChange}
                      placeholder="Enter account holder name"
                      required
                      className="form-field"
                    />
                  </Form.Group>
                </Col>
              </Row>
            </div>

            <div className="form-section mb-4">
              <h5 className="section-title border-bottom pb-2 mb-3">Documents</h5>
              <Row>
                <Col md={12}>
                  <Form.Group className="mb-3">
                    <Form.Label>PAN Document *</Form.Label>
                    <div className="upload-box border border-dashed rounded p-4 text-center">
                      <div className="upload-icon mb-2 mx-auto">
                        <i className="fa fa-file-upload fa-2x text-primary"></i>
                      </div>
                      <p className="mb-1">Click to upload or drag and drop</p>
                      <p className="text-muted small">PDF, JPG, JPEG, or PNG</p>
                      <Form.Control
                        type="file"
                        accept=".pdf,.jpg,.jpeg,.png"
                        onChange={handleFileChange}
                        required
                        className="d-none"
                        id="file-upload"
                      />
                      <Button 
                        variant="outline-primary" 
                        size="sm" 
                        onClick={() => document.getElementById('file-upload').click()}
                      >
                        Choose File
                      </Button>
                    </div>
                    {fileSelected && (
                      <div className="mt-2 text-center">
                        <span className="text-success">Selected file: {fileName}</span>
                      </div>
                    )}
                  </Form.Group>
                </Col>
              </Row>
            </div>

            <div className="application-process p-3 bg-light rounded mb-4 text-center">
              <h6 className="mb-2">Application Process</h6>
              <p className="mb-0 small text-muted">
                Your application will be submitted to the admin for review. You will receive an email
                notification once your application is approved or if additional information is required.
              </p>
            </div>

            <div className="form-actions d-flex justify-content-center gap-3 mt-4">
              <Button
                variant="outline-secondary"
                onClick={() => navigate("/login")}
                className="px-4"
              >
                Cancel
              </Button>
              <Button
                variant="primary"
                type="submit"
                disabled={loading}
                className="px-4"
              >
                {loading ? "Submitting..." : "Submit Application"}
              </Button>
            </div>
          </div>
        </Form>
      </Card.Body>
    </Card>
  );
};

export default ApplicationForm;
