import React, { useState } from 'react';

const VendorRegistrationForm = () => {
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [passwordStrength, setPasswordStrength] = useState({
    score: 0,
    color: "danger",
    message: "Too weak"
  });

  const checkPasswordStrength = (password) => {
    let score = 0;
    let color = "danger";
    let message = "Too weak";

    // Length check
    if (password.length >= 8) score++;

    // Uppercase check
    if (/[A-Z]/.test(password)) score++;

    // Lowercase check
    if (/[a-z]/.test(password)) score++;

    // Number check
    if (/[0-9]/.test(password)) score++;

    // Special character check
    if (/[^A-Za-z0-9]/.test(password)) score++;

    // Set color and message based on score
    if (score === 5) {
      color = "success";
      message = "Very strong";
    } else if (score === 4) {
      color = "info";
      message = "Strong";
    } else if (score === 3) {
      color = "warning";
      message = "Moderate";
    } else if (score === 2) {
      color = "danger";
      message = "Weak";
    }

    setPasswordStrength({ score, color, message });
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'password') {
      checkPasswordStrength(value);
    }
    // ... rest of handleChange function
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    
    // Validate password strength
    if (passwordStrength.score < 3) {
      setError("Please create a stronger password with at least 8 characters, including uppercase, lowercase, numbers, and special characters");
      setLoading(false);
      return;
    }
    
    // ... rest of the handleSubmit function
  };

  // ... rest of the component
};

export default VendorRegistrationForm;
