// Update the form container in ApplicationForm.js
import React, { useState, useEffect } from "react";
import { Form, Button, Row, Col, Alert, Card } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { FaUser, FaBuilding, FaUniversity, FaFileAlt, FaInfoCircle } from "react-icons/fa";

const ApplicationForm = ({ role }) => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [fileSelected, setFileSelected] = useState(false);
  const [fileName, setFileName] = useState("");
  
  const [formData, setFormData] = useState({
    businessName: "",
    pan: "",
    email: "",
    phone: "",
    province: "",
    district: "",
    category: "", // Only required for centers
    contactPerson1: {
      name: "",
      phone: "",
    },
    contactPerson2: {
      name: "",
      phone: "",
    },
    bankDetails: {
      bankName: "",
      accountNumber: "",
      branch: "",
      accountHolderName: "",
    },
    panDocument: null,
    applicationType: role,
  });

  // List of provinces in Nepal
  const provinces = [
    "Province 1",
    "Madhesh Province",
    "Bagmati Province",
    "Gandaki Province",
    "Lumbini Province",
    "Karnali Province",
    "Sudurpashchim Province",
  ];

  // Categories for centers
  const centerCategories = [
    "Government",
    "Private",
    "Non-Profit",
    "Educational",
    "Healthcare",
    "Other"
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    
    // Handle nested objects
    if (name.includes(".")) {
      const [parent, child] = name.split(".");
      setFormData({
        ...formData,
        [parent]: {
          ...formData[parent],
          [child]: value,
        },
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFormData({
        ...formData,
        panDocument: file,
      });
      setFileSelected(true);
      setFileName(file.name);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");
    setSuccess("");

    // Validate form data
    if (!formData.businessName || !formData.pan || !formData.email || !formData.phone) {
      setError("Please fill in all required fields");
      setLoading(false);
      return;
    }

    // Validate PAN format (assuming PAN is a 9-digit number in Nepal)
    const panRegex = /^[0-9]{9}$/;
    if (!panRegex.test(formData.pan)) {
      setError("PAN must be a 9-digit number");
      setLoading(false);
      return;
    }

    // Validate email format
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(formData.email)) {
      setError("Please enter a valid email address");
      setLoading(false);
      return;
    }

    // Validate phone number (assuming 10-digit number for Nepal)
    const phoneRegex = /^[0-9]{10}$/;
    if (!phoneRegex.test(formData.phone)) {
      setError("Phone number must be a 10-digit number");
      setLoading(false);
      return;
    }

    // Validate category for centers
    if (role === "center" && !formData.category) {
      setError("Please select a category for your center");
      setLoading(false);
      return;
    }

    // Create form data for file upload
    const submitData = new FormData();
    for (const key in formData) {
      if (key === "contactPerson1" || key === "contactPerson2" || key === "bankDetails") {
        submitData.append(key, JSON.stringify(formData[key]));
      } else if (key === "panDocument" && formData[key]) {
        submitData.append("panDocument", formData[key]);
      } else {
        submitData.append(key, formData[key]);
      }
    }

    try {
      const res = await axios.post("/api/applications", submitData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      setSuccess(
        `Your ${role === "vendor" ? "vendor" : "center"} application has been submitted successfully! We will review your application and get back to you soon.`
      );
      
      // Reset form after successful submission
      setFormData({
        businessName: "",
        pan: "",
        email: "",
        phone: "",
        province: "",
        district: "",
        category: "",
        contactPerson1: {
          name: "",
          phone: "",
        },
        contactPerson2: {
          name: "",
          phone: "",
        },
        bankDetails: {
          bankName: "",
          accountNumber: "",
          branch: "",
          accountHolderName: "",
        },
        panDocument: null,
        applicationType: role,
      });
      setFileSelected(false);
      setFileName("");
      
      // Redirect to dashboard after 3 seconds
      setTimeout(() => {
        navigate(`/${role}/dashboard`);
      }, 3000);
    } catch (err) {
      setError(
        err.response?.data?.message || "An error occurred while submitting your application"
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="registration-page d-flex align-items-center justify-content-center min-vh-100 py-5">
      <div className="registration-container">
        <Card className="registration-card shadow-lg border-0">
          <Card.Body className="p-0">
            {/* Logo and Header Section */}
            <div className="text-center py-4 border-bottom bg-light">
              <img
                src="/assets/images/vrslogo.png"
                alt="VRS Logo"
                style={{ width: "120px", height: "auto" }}
                className="mb-3"
              />
              <h2 className="fw-bold">
                {role === "vendor" ? "Vendor" : "Center"} Registration
              </h2>
              <p className="text-muted px-4">
                {role === "vendor"
                  ? "Complete this form to register as a vendor and start submitting applications to centers."
                  : "Complete this form to register as a center and start receiving vendor applications."}
              </p>
            </div>

            <div className="form-container mx-auto p-4">
              {error && <Alert variant="danger">{error}</Alert>}
              {success && <Alert variant="success">{success}</Alert>}

              <Form onSubmit={handleSubmit}>
                <Card className="mb-4 border-0 shadow-sm bg-white">
                  <Card.Body>
                    <div className="form-section mb-4 bg-white">
                      <div className="section-header">
                        <i><FaBuilding size={20} /></i>
                        <h5>Business Information</h5>
                      </div>
                      <Row className="justify-content-center">
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Business Name *</Form.Label>
                            <Form.Control
                              type="text"
                              name="businessName"
                              value={formData.businessName}
                              onChange={handleChange}
                              placeholder="Enter business name"
                              required
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>PAN Number *</Form.Label>
                            <Form.Control
                              type="text"
                              name="pan"
                              value={formData.pan}
                              onChange={handleChange}
                              placeholder="Enter 9-digit PAN number"
                              required
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                      </Row>

                      <Row className="justify-content-center">
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Email Address *</Form.Label>
                            <Form.Control
                              type="email"
                              name="email"
                              value={formData.email}
                              onChange={handleChange}
                              placeholder="Enter email address"
                              required
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Phone Number *</Form.Label>
                            <div className="input-group">
                              <span className="input-group-text">+977</span>
                              <Form.Control
                                type="text"
                                name="phone"
                                value={formData.phone}
                                onChange={handleChange}
                                placeholder="Enter 10-digit phone number"
                                required
                                className="form-control-lg"
                              />
                            </div>
                          </Form.Group>
                        </Col>
                      </Row>

                      <Row className="justify-content-center">
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Province *</Form.Label>
                            <Form.Select
                              name="province"
                              value={formData.province}
                              onChange={handleChange}
                              required
                              className="form-control-lg"
                            >
                              <option value="">Select Province</option>
                              {provinces.map((province) => (
                                <option key={province} value={province}>
                                  {province}
                                </option>
                              ))}
                            </Form.Select>
                          </Form.Group>
                        </Col>
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>District *</Form.Label>
                            <Form.Control
                              type="text"
                              name="district"
                              value={formData.district}
                              onChange={handleChange}
                              placeholder="Enter district"
                              required
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                      </Row>

                      {/* Category field for centers only */}
                      {role === "center" && (
                        <Row className="justify-content-center">
                          <Col md={12}>
                            <Form.Group className="mb-3">
                              <Form.Label>Center Category *</Form.Label>
                              <Form.Select
                                name="category"
                                value={formData.category}
                                onChange={handleChange}
                                required
                                className="form-control-lg"
                              >
                                <option value="">Select Category</option>
                                {centerCategories.map((category) => (
                                  <option key={category} value={category}>
                                    {category}
                                  </option>
                                ))}
                              </Form.Select>
                            </Form.Group>
                          </Col>
                        </Row>
                      )}
                    </div>

                    <div className="form-section mb-4 bg-white">
                      <div className="section-header">
                        <i><FaUser size={20} /></i>
                        <h5>Contact Persons</h5>
                      </div>
                      <Row className="justify-content-center">
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Contact Person 1 Name *</Form.Label>
                            <Form.Control
                              type="text"
                              name="contactPerson1.name"
                              value={formData.contactPerson1.name}
                              onChange={handleChange}
                              placeholder="Enter contact person name"
                              required
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Contact Person 1 Phone *</Form.Label>
                            <div className="input-group">
                              <span className="input-group-text">+977</span>
                              <Form.Control
                                type="text"
                                name="contactPerson1.phone"
                                value={formData.contactPerson1.phone}
                                onChange={handleChange}
                                placeholder="Enter contact person phone"
                                required
                                className="form-control-lg"
                              />
                            </div>
                          </Form.Group>
                        </Col>
                      </Row>

                      <Row className="justify-content-center">
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Contact Person 2 Name (Optional)</Form.Label>
                            <Form.Control
                              type="text"
                              name="contactPerson2.name"
                              value={formData.contactPerson2.name}
                              onChange={handleChange}
                              placeholder="Enter contact person name"
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Contact Person 2 Phone (Optional)</Form.Label>
                            <div className="input-group">
                              <span className="input-group-text">+977</span>
                              <Form.Control
                                type="text"
                                name="contactPerson2.phone"
                                value={formData.contactPerson2.phone}
                                onChange={handleChange}
                                placeholder="Enter contact person phone"
                                className="form-control-lg"
                              />
                            </div>
                          </Form.Group>
                        </Col>
                      </Row>
                    </div>

                    <div className="form-section mb-4 bg-white">
                      <div className="section-header">
                        <i><FaUniversity size={20} /></i>
                        <h5>Bank Details</h5>
                      </div>
                      <Row className="justify-content-center">
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Bank Name *</Form.Label>
                            <Form.Control
                              type="text"
                              name="bankDetails.bankName"
                              value={formData.bankDetails.bankName}
                              onChange={handleChange}
                              placeholder="Enter bank name"
                              required
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Account Number *</Form.Label>
                            <Form.Control
                              type="text"
                              name="bankDetails.accountNumber"
                              value={formData.bankDetails.accountNumber}
                              onChange={handleChange}
                              placeholder="Enter account number"
                              required
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                      </Row>

                      <Row className="justify-content-center">
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Branch *</Form.Label>
                            <Form.Control
                              type="text"
                              name="bankDetails.branch"
                              value={formData.bankDetails.branch}
                              onChange={handleChange}
                              placeholder="Enter branch name"
                              required
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                        <Col md={6}>
                          <Form.Group className="mb-3">
                            <Form.Label>Account Holder Name *</Form.Label>
                            <Form.Control
                              type="text"
                              name="bankDetails.accountHolderName"
                              value={formData.bankDetails.accountHolderName}
                              onChange={handleChange}
                              placeholder="Enter account holder name"
                              required
                              className="form-control-lg"
                            />
                          </Form.Group>
                        </Col>
                      </Row>
                    </div>

                    <div className="form-section mb-4 bg-white">
                      <div className="section-header">
                        <i><FaFileAlt size={20} /></i>
                        <h5>Documents</h5>
                      </div>
                      <Row className="justify-content-center">
                        <Col md={10}>
                          <Form.Group className="mb-3">
                            <Form.Label>PAN Document *</Form.Label>
                            <div className="upload-box border border-dashed rounded p-4 text-center">
                              <div className="upload-icon mb-2 mx-auto">
                                <FaFileAlt size={24} className="text-primary" />
                              </div>
                              <p className="mb-1">Click to upload or drag and drop</p>
                              <p className="text-muted small">PDF, JPG, JPEG, or PNG</p>
                              <Form.Control
                                type="file"
                                accept=".pdf,.jpg,.jpeg,.png"
                                onChange={handleFileChange}
                                required
                                className="d-none"
                                id="file-upload"
                              />
                              <Button 
                                variant="outline-primary" 
                                size="sm" 
                                onClick={() => document.getElementById('file-upload').click()}
                              >
                                Choose File
                              </Button>
                            </div>
                            {fileSelected && (
                              <div className="mt-2 text-center">
                                <span className="text-success">Selected file: {fileName}</span>
                              </div>
                            )}
                          </Form.Group>
                        </Col>
                      </Row>
                    </div>

                    <div className="application-process mx-auto bg-white">
                      <div className="d-flex align-items-center justify-content-center mb-2">
                        <FaInfoCircle className="text-primary me-2" size={18} />
                        <h6 className="mb-0 fw-bold">Application Process</h6>
                      </div>
                      <p className="mb-0 small text-muted text-center">
                        Your application will be submitted to the admin for review. You will receive an email
                        notification once your application is approved or if additional information is required.
                      </p>
                    </div>

                    <div className="form-actions">
                      <Button
                        variant="outline-secondary"
                        onClick={() => navigate("/login")}
                        className="px-4 py-2"
                      >
                        Cancel
                      </Button>
                      <Button
                        variant="primary"
                        type="submit"
                        disabled={loading}
                        className="px-4 py-2"
                      >
                        {loading ? "Submitting..." : "Submit Application"}
                      </Button>
                    </div>
                  </Card.Body>
                </Card>
              </Form>
            </div>
          </Card.Body>
        </Card>
      </div>
    </div>
  );
};

export default ApplicationForm;
